function MakeField($type, $name, $value, $list="", $default=""){

if($value ==""){ $value = $default; }
	switch($type){
	
		case "checkbox": { return  "<input type='checkbox' class='checkbox' name='".$name."' value='".$value."'> "; } break;
	
		case "text": { return  "<input type='text' name='adminArray[".$name."]' value='".$value."' style='width:100%'>"; } break;

		case "textarea": { return "<textarea name='adminArray[".$name."]' type='text' style='width:100%; height:150px;'>".stripslashes($value)."</textarea>"; } break;


		case "listbox": { 

			$r ="<select name='adminArray[".$name."]' style='width: 100%'>";
			foreach($list as $key => $val){
				if($value==$key){ $sel="selected"; }else{ $sel=""; }
				$r .="<option value='".$key."' ".$sel.">".$val."</option>";
			}
			$r .="</select>";
			return $r;

		} break;

	}

}

