function trimAmountForEpay($amount) {
	$res = "";
	for ($i = 0; $i < strlen($amount); $i++) {
		$letter = substr($amount, $i, 1);
		if ($letter == "0" || $letter == "1" || $letter == "2" || $letter == "3" || $letter == "4" || $letter == "5" || $letter == "6" || $letter == "7" || $letter == "8" || $letter == "9") {
			$res = ($res . $letter);
		}
	}
	return $res;
}


